                            print(f"   ‚ö†Ô∏è  {name}: Running but not responding")
                    except requests.exceptions.RequestException:
                        print(f"   ‚ö†Ô∏è  {name}: Running but not responding to health check")
                else:
                    # Non-web services (like queue processors)
                    print(f"   ‚úÖ {name}: Running (queue processor)")
            else:
                print(f"   ‚ùå {name}: Process died")

    def stop_all_services(self):
        """Stop all running services"""
        print("\nüõë Stopping all services...")

        for name, info in self.processes.items():
            try:
                process = info['process']
                if process.poll() is None:  # Process is still running
                    print(f"   Stopping {name}...")
                    process.terminate()

                    # Wait for graceful shutdown
                    try:
                        process.wait(timeout=10)
                        print(f"   ‚úÖ {name} stopped gracefully")
                    except subprocess.TimeoutExpired:
                        print(f"   üî• Force killing {name}...")
                        process.kill()
                        process.wait()
                        print(f"   ‚úÖ {name} force stopped")
                else:
                    print(f"   ‚úÖ {name} already stopped")

            except Exception as e:
                print(f"   ‚ùå Error stopping {name}: {e}")

    def show_status(self):
        """Show current service status"""
        print("\nüìä Service Status:")
        print("=" * 60)

        if not self.processes:
            print("   No services running")
            return

        for name, info in self.processes.items():
            if info['process'].poll() is None:
                print(f"   ‚úÖ {name}")
                print(f"      PID: {info['process'].pid}")
                print(f"      Port: {info['port']}")
                print(f"      Log: {info['log_file']}")
            else:
                print(f"   ‚ùå {name} (stopped)")

        print(f"\nüéØ Access Points:")
        print(f"   Mobile PWA: http://localhost:{self.config['services']['pwa_backend']['port']}")
        print(f"   Admin Panel: http://localhost:{self.config['services']['annotation_server']['port']}")
        print(f"   Desktop UI: python src/ui/revolutionary_shell.py")

    def monitor_services(self):
        """Monitor running services"""
        print(f"\nüéâ Revolutionary Card Grader Pro is running!")
        self.show_status()
        print(f"\nüí° Commands:")
        print(f"   - View logs: tail -f logs/*.log")
        print(f"   - Check status: python services/start_system.py status")
        print(f"   - Stop all: python services/start_system.py stop")
        print(f"\nPress Ctrl+C to stop all services...")

        # Setup signal handlers
        def signal_handler(signum, frame):
            print(f"\n\nüõë Received signal {signum}, shutting down...")
            self.stop_all_services()
            sys.exit(0)

        signal.signal(signal.SIGINT, signal_handler)
        signal.signal(signal.SIGTERM, signal_handler)

        try:
            while True:
                time.sleep(5)

                # Check for dead processes
                for name, info in list(self.processes.items()):
                    if info['process'].poll() is not None:
                        print(f"\n‚ö†Ô∏è  {name} has stopped unexpectedly!")

        except KeyboardInterrupt:
            pass

    def run(self, command=None):
        """Main run method"""
        if command == "stop":
            self.stop_all_services()
            return

        if command == "status":
            self.show_status()
            return

        # Full startup sequence
        print("üöÄ Revolutionary Card Grader Pro - System Startup")
        print("=" * 55)

        # Check system requirements
        if not self.check_system_requirements():
            print("\n‚ùå System requirements not met!")
            print("üí° Run: python scripts/start_dev_services.py setup")
            sys.exit(1)

        # Create directories
        self.create_directories()

        # Start services
        self.start_all_services()

        # Wait a moment for services to initialize
        time.sleep(5)

        # Check health
        self.check_service_health()

        # Monitor
        self.monitor_services()

