            'grade_distribution': grades,
            'most_common_grade': max(grades.items(), key=lambda x: x[1])[0] if grades else None
        })

    except Exception as e:
        return jsonify({
            'success': False,
            'error': 'Failed to calculate stats',
            'message': str(e)
        }), 500

